export class RootComponent {
  titleService;
  authService;
  appState;
  appModes;
  electronService;
  router;
  log;
  updateService;
  detailsService;
  identityService;
  settings;
  apiService;
  walletService;
  wallet;
  cd;
  dialog;
  ngZone;
  storage;
  chains;
  notifications;
  globalService;
  zone;
  breakpointObserver;
  hostClass = true; // private readonly destroyed$ = new Subject<void>();

  walletObservable;
  ipc;
  handset = false;
  title = 'app';
  showFiller = true;
  isActive = false;
  coinName;
  coinIcon;
  percentSyncedNumber = 0;
  percentSynced = '0%';
  generalInfo;
  isAuthenticated;
  menuMode = 'side';
  menuOpened = true; // TODO: Change into Observable.
  // get userActivated(): boolean {
  //   return this.authService.authenticated;
  // }

  constructor(titleService, authService, appState, appModes, electronService, router, log, updateService, detailsService, identityService, settings, apiService, walletService, wallet, cd, dialog, ngZone, storage, chains, notifications, globalService, zone, breakpointObserver) {
    this.titleService = titleService;
    this.authService = authService;
    this.appState = appState;
    this.appModes = appModes;
    this.electronService = electronService;
    this.router = router;
    this.log = log;
    this.updateService = updateService;
    this.detailsService = detailsService;
    this.identityService = identityService;
    this.settings = settings;
    this.apiService = apiService;
    this.walletService = walletService;
    this.wallet = wallet;
    this.cd = cd;
    this.dialog = dialog;
    this.ngZone = ngZone;
    this.storage = storage;
    this.chains = chains;
    this.notifications = notifications;
    this.globalService = globalService;
    this.zone = zone;
    this.breakpointObserver = breakpointObserver;
    this.log.info('Expanded:', localStorage.getItem('Menu:Expanded'));
    this.loadFiller();
    this.isAuthenticated = authService.isAuthenticated();

    if (this.electronService.ipcRenderer) {
      this.appState.setVersion(APP_VERSION);
      this.log.info('application version: ', this.appState.version);
      this.ipc = electronService.ipcRenderer;
      this.ipc.on('daemon-exiting', (event, error) => {
        this.log.info('daemon is currently being stopped... please wait...');
        this.appState.shutdownInProgress = true;
        this.cd.detectChanges(); // If the exit takes a very long time, we want to allow users to forcefully exit Blockcore Hub.

        setTimeout(() => {
          this.appState.shutdownDelayed = true;
          this.cd.detectChanges();
        }, 60000);
      });
      this.ipc.on('daemon-exited', (event, error) => {
        this.log.info('daemon is stopped.');
        this.appState.shutdownInProgress = false;
        this.appState.shutdownDelayed = false; // Perform a new close event on the window, this time it will close itself.

        window.close();
      });
      this.ipc.on('daemon-changing', (event, error) => {
        this.zone.run(() => {
          this.log.info('daemon change is requested and shutdown was successful.');
          this.cd.detectChanges(); // Navigate again to hide the loading indicator.
          // if (!this.appState.isChangingToChain) {

          this.router.navigate(['/load']); // }

          this.cd.detectChanges();
        });
      });
      this.ipc.on('daemon-error', (event, error) => {
        this.log.error(error);
        const dialogRef = this.dialog.open(ReportComponent, {
          data: {
            title: 'Failed to start node background daemon',
            error,
            lines: this.log.lastEntries()
          }
        });
        dialogRef.afterClosed().subscribe(result => {
          this.log.info(`Dialog result: ${result}`);
        });
      });
      this.ipc.on('log-debug', (event, msg) => {
        this.log.verbose(msg);
      });
      this.ipc.on('log-info', (event, msg) => {
